sp_header_footer.client_script:
function FlightOpsHeaderController($window) {
  var c = this;
  c.handlePrimaryAction = function() {
    if (c.data.primaryCta.link) {
      $window.location = c.data.primaryCta.link;
    }
  };
  c.handleSecondaryAction = function() {
    if (c.data.secondaryCta.link) {
      $window.location = c.data.secondaryCta.link;
    }
  };
}

