sp_header_footer.script:
(function() {
  data.eyebrow = options.eyebrow || 'AI-Enhanced Travel';
  data.title = options.title || 'FlightOps 360';
  data.subtitle = options.subtitle || 'Book flights, manage crews, and monitor operations in one workspace.';
  data.primaryCta = {
    label: options.primary_cta_label || 'Book a Flight',
    link: options.primary_cta_link || '?id=sc_cat_item&sys_id={{CAT_ITEM_SYS_ID}}'
  };
  data.secondaryCta = {
    label: options.secondary_cta_label || 'View Dashboard',
    link: options.secondary_cta_link || '?id=flightops_dashboard'
  };
  data.heroImage = options.hero_image || '';
})();
