sys_security_acl.script:
(function executeRule(current, previous) {

    // RULE 1: Admins can see everything
    if (gs.hasRole('admin')) {
        return true;
    }

    // RULE 2: Operations managers can see everything
    if (gs.hasRole('x_1603912_flightop.ops_manager')) {
        return true;
    }

    // RULE 3: Customers can see only THEIR bookings
    if (gs.hasRole('x_1603912_flightop.customer')) {
        var currentUserId = gs.getUserID();
        var bookingCustomerId = current.getValue('customer_id');
        return (bookingCustomerId == currentUserId);
    }

    // RULE 4: Everyone else is denied
    return false;

})(current, previous);
